export const dynamic = "force-dynamic"

export async function POST(request: Request) {
  try {
    const data = await request.json()
    const pncpResponse = await fetch(process.env.PNCP_API_URL!, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${process.env.PNCP_API_TOKEN}`
      },
      body: JSON.stringify(data)
    })
    
    if (!pncpResponse.ok) {
      throw new Error('PNCP API error')
    }
    
    const result = await pncpResponse.json()
    return Response.json(result)
  } catch (error) {
    console.error('Error syncing with PNCP:', error)
    return Response.json({ error: 'Failed to sync with PNCP' }, { status: 500 })
  }
}