import { prisma } from "@/lib/prisma"

export const dynamic = "force-dynamic"

export async function POST(
  request: Request,
  { params }: { params: { id: string } }
) {
  try {
    const process = await prisma.procurementProcess.update({
      where: {
        id: params.id
      },
      data: {
        status: "COMPLETED"
      }
    })
    
    // Sync with PNCP
    await fetch('/api/pncp', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        type: 'END_BIDDING',
        processId: params.id
      })
    })
    
    return Response.json(process)
  } catch (error) {
    console.error('Error ending bidding:', error)
    return Response.json({ error: 'Failed to end bidding' }, { status: 500 })
  }
}