import { prisma } from "@/lib/prisma"

export const dynamic = "force-dynamic"

export async function GET() {
  try {
    const processes = await prisma.procurementProcess.findMany({
      include: {
        organization: true,
        bids: true,
        contracts: true,
      },
    })
    return Response.json(processes)
  } catch (error) {
    console.error('Error fetching processes:', error)
    return Response.json({ error: 'Failed to fetch processes' }, { status: 500 })
  }
}

export async function POST(request: Request) {
  try {
    const data = await request.json()
    const process = await prisma.procurementProcess.create({
      data: {
        ...data,
        status: "DRAFT",
      },
    })
    return Response.json(process)
  } catch (error) {
    console.error('Error creating process:', error)
    return Response.json({ error: 'Failed to create process' }, { status: 500 })
  }
}