export interface User {
  id: string;
  name: string;
  email: string;
  role: "admin" | "agency_admin" | "auctioneer" | "contract_manager" | "supplier";
  organization?: Organization;
  createdAt: Date;
  updatedAt: Date;
}

export interface Organization {
  id: string;
  name: string;
  type: "public_agency" | "supplier";
  cnpj: string;
  email: string;
  phone: string;
  address: string;
  createdAt: Date;
  updatedAt: Date;
}

export interface Bid {
  id: string;
  number: string;
  year: number;
  object: string;
  type: "pregao" | "concorrencia" | "tomada_preco" | "convite";
  mode: "electronic" | "presential";
  status: "draft" | "published" | "in_progress" | "completed" | "cancelled";
  estimatedValue: number;
  openingDate: Date;
  organization: Organization;
  createdAt: Date;
  updatedAt: Date;
}

export interface Contract {
  id: string;
  number: string;
  year: number;
  object: string;
  value: number;
  startDate: Date;
  endDate: Date;
  status: "active" | "completed" | "cancelled";
  bid: Bid;
  supplier: Organization;
  organization: Organization;
  createdAt: Date;
  updatedAt: Date;
}